var test = require('tape');
var traverse = require('../');

test('stop', function (t) {
    var visits = 0;
    traverse('abcdefghij'.split('')).forEach(function (node) {
        if (typeof node === 'string') {
            visits ++;
            if (node === 'e') this.stop()
        }
    });
    
    t.equal(visits, 5);
    t.end();
});

